// var formidable = require('formidable');
var multiparty = require('multiparty');
var fs = require('fs');  //node.js核心的文件处理模块
function upload(req, res, fn){
  var message = '';
 // var form = new formidable.IncomingForm();   //创建上传表单
  var form = new multiparty.Form();//新建表单
    form.encoding = 'utf-8';        //设置编辑
    form.uploadDir = 'public/upload/';     //设置上传目录
    form.keepExtensions = true;     //保留后缀
    form.maxFieldsSize = 2 * 1024 * 1024;   //文件大小 
    form.parse(req, function(err, fields, files) {
    if (err) {
      console.log(err);
    }  
      if(files.courseData){
         //重命名资料名
        var filename1 = files.courseData[0].originalFilename;//文件名
        var avatarName1= reName(filename1);
        var dataUrl = form.uploadDir + avatarName1 ;//新名称
        fs.renameSync(files.courseData[0].path, dataUrl);  //重命名
      }
      //重命名相片名
      var filename2 = files.photo[0].originalFilename;//文件名
      var avatarName2= reName(filename2);
      var newPath= form.uploadDir + avatarName2;
      fs.renameSync(files.photo[0].path, newPath); 
      var photoUrl =newPath.slice(6); //相片文件名
      fn.call(this,fields,dataUrl,photoUrl);
  });
};
exports.download = function(req, res){
  var path = 'public/upload/upload_e80d52d8d6883ef37bedce941e14a610.zip';  // 文件存储的路径

  // filename:设置下载时文件的文件名，可不填，则为原名称
  res.download(path); 
};
 function reName(filename){
  // 对文件名进行处理，以应对上传同名文件的情况
      var nameArray = filename.split('.');
      var type = nameArray[nameArray.length-1];//后缀
      var name = '';
      for(var i=0; i<nameArray.length-1; i++){
          name = name + nameArray[i];
      }
      var rand = Math.random()*100 + 900;
      var num = parseInt(rand, 10);
      var avatarName = name + num +  '.' + type;
      return avatarName;
 }
module.exports=upload;
