var dbUtil = require("dbUtil");
var getDataUtil = {
	getRegisterData:function(params,url){
		url = url.slice(6);//切割掉public
		//var preve = ".."//给地址加上../可以访问到public
		var stu = {};
		stu.username = params.username[0];
		stu.password = params.password[0];
		stu.gender = params.gender[0];
		stu.descript = params.descript[0];
		stu.email = params.email[0];
		stu.photo =url;
		return stu;
	},
	getAllDepartment:function(req,res){
		var sql = "select * from tbl_department";
		dbUtil.excute(sql,null,function(results){
			req.session.department = results;
		});
	},
	getAllLevels:function(req,res){
		var sql = "select * from tbl_levels";
		dbUtil.excute(sql,null,function(results){
			req.session.levels = results;
		});
	},
	getAllTopics:function(req,res){
		var sql = "select * from tbl_topics";
		dbUtil.excute(sql,null,function(results){
			req.session.topics = results;
		});
	},
	getAllAnnouncement:function(req,res){
		var sql = "select * from tbl_Announcement";
		dbUtil.excute(sql,null,function(results){
			req.session.announcement = results;
		});
	}
}
module.exports = getDataUtil;